@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }

  /* Enhanced scrolling styles */
  .scrollbar-thin {
    scrollbar-width: thin;
    scrollbar-color: hsl(var(--muted-foreground)) transparent;
  }

  .scrollbar-thin::-webkit-scrollbar {
    width: 8px;
    height: 8px;
  }

  .scrollbar-thin::-webkit-scrollbar-track {
    background: transparent;
  }

  .scrollbar-thin::-webkit-scrollbar-thumb {
    background-color: hsl(var(--muted-foreground));
    border-radius: 4px;
    border: 2px solid transparent;
    background-clip: content-box;
  }

  .scrollbar-thin::-webkit-scrollbar-thumb:hover {
    background-color: hsl(var(--foreground));
  }

  .scrollbar-track-transparent::-webkit-scrollbar-track {
    background: transparent;
  }

  .scrollbar-thumb-gray-300::-webkit-scrollbar-thumb {
    background-color: rgb(209, 213, 219);
  }

  /* Dashboard specific styles */
  .dashboard-container {
    overflow-x: auto;
    overflow-y: auto;
    min-height: 100%;
    scroll-behavior: smooth;
  }

  /* Responsive grid improvements */
  .dashboard-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 1.5rem;
  }

  .chart-container {
    position: relative;
    height: 320px;
    width: 100%;
  }

  /* Kanban board specific styles */
  .kanban-column {
    min-height: 500px;
    overflow-y: auto;
  }

  .story-card {
    transition: all 0.2s ease-in-out;
  }

  .story-card:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px -8px rgba(0, 0, 0, 0.1);
  }

  /* Utility classes for text truncation */
  .line-clamp-1 {
    display: -webkit-box;
    -webkit-line-clamp: 1;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  .line-clamp-2 {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  .line-clamp-3 {
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  /* Responsive card scaling */
  @media (max-width: 640px) {
    .story-card {
      transform: scale(0.9);
      transform-origin: center;
    }
  }

  @media (min-width: 1920px) {
    .story-card.comfortable {
      transform: scale(1.1);
      transform-origin: center;
    }
  }

  /* Zoom level responsive adjustments */
  @media (resolution >= 120dpi) and (resolution < 144dpi) {
    .story-card {
      font-size: 0.9em;
    }
  }

  @media (resolution >= 144dpi) {
    .story-card {
      font-size: 0.8em;
    }
  }

  /* Zoom-based responsive scaling */
  @media (max-width: 768px) {
    .dashboard-container {
      padding: 1rem;
    }

    .dashboard-grid {
      grid-template-columns: 1fr;
    }
  }

  @media (min-width: 1920px) {
    .dashboard-container {
      max-width: none;
    }

    .dashboard-grid {
      grid-template-columns: repeat(4, 1fr);
    }
  }
}

/* Chart.js import */
html {
  scroll-behavior: smooth;
  overflow-x: auto;
}

/* Smooth scrolling for all containers */
* {
  scroll-behavior: smooth;
}

