import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { type StoryWithAssignee, type User } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/use-auth";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { 
  MessageCircle, 
  Star, 
  Calendar,
  User as UserIcon,
  Clock,
  ArrowUp,
  ArrowDown,
  Flag,
  BookOpen,
  Bug,
  Zap
} from "lucide-react";
import { cn } from "@/lib/utils";
import { formatDistanceToNow } from "date-fns";

interface ActiveSprintProps {
  onStoryClick?: (storyId: string) => void;
}

const PRIORITY_COLORS = {
  High: "hsl(0 84.2% 60.2%)",
  Medium: "hsl(43 74% 66%)",
  Low: "hsl(197 37% 24%)",
};

const PRIORITY_ICONS = {
  High: ArrowUp,
  Medium: Flag,
  Low: ArrowDown,
};

const STORY_TYPE_ICONS = {
  Story: BookOpen,
  Bug: Bug,
  Epic: Zap,
};

export function ActiveSprint({ onStoryClick }: ActiveSprintProps) {
  const [selectedUserId, setSelectedUserId] = useState<string>("all");
  const { user } = useAuth();

  const { data: stories, isLoading: storiesLoading } = useQuery<StoryWithAssignee[]>({
    queryKey: ["/api/stories"],
  });

  const { data: users, isLoading: usersLoading } = useQuery<User[]>({
    queryKey: ["/api/users"],
  });

  const getUserInitials = (name: string) => {
    return name
      .split(" ")
      .map(n => n[0])
      .join("")
      .toUpperCase()
      .slice(0, 2);
  };

  // Filter stories for active sprint
  const activeSprintStories = stories?.filter(story => 
    story.sprintId === "sprint-1" && // Current active sprint
    (selectedUserId === "all" || story.assigneeId === selectedUserId)
  ) || [];

  // Group stories by assignee
  const storiesByUser = activeSprintStories.reduce((acc, story) => {
    const userId = story.assigneeId || "unassigned";
    if (!acc[userId]) {
      acc[userId] = [];
    }
    acc[userId].push(story);
    return acc;
  }, {} as Record<string, StoryWithAssignee[]>);

  if (storiesLoading || usersLoading) {
    return (
      <div className="p-6">
        <div className="flex items-center justify-between mb-6">
          <Skeleton className="h-8 w-48" />
          <Skeleton className="h-10 w-64" />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {[...Array(6)].map((_, i) => (
            <Skeleton key={i} className="h-48" />
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center space-x-3">
          <Clock className="w-6 h-6 text-primary" />
          <h2 className="text-2xl font-bold text-foreground">Active Sprint</h2>
          <Badge variant="secondary" className="text-sm">
            {activeSprintStories.length} stories
          </Badge>
        </div>

        <div className="flex items-center space-x-4">
          <Label htmlFor="user-filter" className="text-sm font-medium">
            Filter by User:
          </Label>
          <Select
            value={selectedUserId}
            onValueChange={setSelectedUserId}
          >
            <SelectTrigger className="w-64" id="user-filter">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Users</SelectItem>
              {users?.map((user) => (
                <SelectItem key={user.id} value={user.id}>
                  <div className="flex items-center space-x-2">
                    <Avatar className="w-5 h-5">
                      <AvatarFallback className="bg-primary text-primary-foreground text-xs">
                        {getUserInitials(user.name)}
                      </AvatarFallback>
                    </Avatar>
                    <span>{user.name}</span>
                  </div>
                </SelectItem>
              ))}
              <SelectItem value="unassigned">Unassigned</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </div>

      {activeSprintStories.length === 0 ? (
        <div className="text-center py-12">
          <Clock className="w-16 h-16 text-muted-foreground mx-auto mb-4" />
          <h3 className="text-lg font-medium text-foreground mb-2">No Active Stories</h3>
          <p className="text-muted-foreground">
            {selectedUserId === "all" 
              ? "There are no stories in the active sprint yet."
              : "The selected user has no stories in the active sprint."
            }
          </p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {activeSprintStories.map((story) => {
            const PriorityIcon = PRIORITY_ICONS[story.priority as keyof typeof PRIORITY_ICONS];
            const TypeIcon = STORY_TYPE_ICONS.Story; // Default to Story since we don't have type field yet

            return (
              <Card
                key={story.id}
                className="cursor-pointer hover:shadow-md transition-all duration-200 hover:bg-muted/30"
                onClick={() => onStoryClick?.(story.id)}
                data-testid={`active-story-card-${story.id}`}
              >
                <CardHeader className="pb-3">
                  <div className="flex items-start justify-between">
                    <div className="flex items-center space-x-2">
                      <TypeIcon className="w-4 h-4 text-blue-500" />
                      <CardTitle className="text-sm font-medium leading-tight">
                        {story.title}
                      </CardTitle>
                    </div>
                    <div className="flex items-center space-x-2">
                      <Badge
                        variant="secondary"
                        className="text-xs px-2 py-1"
                        style={{
                          backgroundColor: `${PRIORITY_COLORS[story.priority as keyof typeof PRIORITY_COLORS]}20`,
                          color: PRIORITY_COLORS[story.priority as keyof typeof PRIORITY_COLORS],
                        }}
                      >
                        <PriorityIcon className="w-3 h-3 mr-1" />
                        {story.priority}
                      </Badge>
                      <Badge variant="outline" className="text-xs">
                        {story.status}
                      </Badge>
                    </div>
                  </div>
                </CardHeader>

                <CardContent className="pt-0">
                  {story.description && (
                    <p className="text-xs text-muted-foreground mb-3 line-clamp-2">
                      {story.description}
                    </p>
                  )}

                  <div className="flex items-center justify-between mb-3">
                    <div className="flex items-center space-x-3">
                      {story.pointer && (
                        <div className="flex items-center space-x-1">
                          <Star className="w-3 h-3 text-muted-foreground" />
                          <span className="text-xs text-muted-foreground">{story.pointer} pts</span>
                        </div>
                      )}
                      <div className="flex items-center space-x-1">
                        <MessageCircle className="w-3 h-3 text-muted-foreground" />
                        <span className="text-xs text-muted-foreground">
                          {story.commentCount || 0}
                        </span>
                      </div>
                    </div>

                    {story.dueDate && (
                      <div className="flex items-center space-x-1 text-xs text-muted-foreground">
                        <Calendar className="w-3 h-3" />
                        <span>
                          {formatDistanceToNow(new Date(story.dueDate), { addSuffix: true })}
                        </span>
                      </div>
                    )}
                  </div>

                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                      {story.assignee ? (
                        <>
                          <Avatar className="w-6 h-6">
                            <AvatarFallback className="bg-primary text-primary-foreground text-xs">
                              {getUserInitials(story.assignee.name)}
                            </AvatarFallback>
                          </Avatar>
                          <span className="text-xs text-foreground font-medium">
                            {story.assignee.name}
                          </span>
                        </>
                      ) : (
                        <div className="flex items-center space-x-2">
                          <div className="w-6 h-6 rounded-full bg-muted flex items-center justify-center">
                            <UserIcon className="w-3 h-3 text-muted-foreground" />
                          </div>
                          <span className="text-xs text-muted-foreground">Unassigned</span>
                        </div>
                      )}
                    </div>

                    <Badge variant="outline" className="text-xs">
                      Sprint 1
                    </Badge>
                  </div>
                </CardContent>
              </Card>
            );
          })}
        </div>
      )}
    </div>
  );
}

client/src/components/comments-sidebar.t.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertCommentSchema, type CommentWithUser, type StoryWithAssignee } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Skeleton } from "@/components/ui/skeleton";
import { X, Send, Trash2, Edit } from "lucide-react";
import { cn } from "@/lib/utils";
import { z } from "zod";
import { formatDistanceToNow } from "date-fns";

interface CommentsSidebarProps {
  isOpen: boolean;
  onClose: () => void;
  storyId: string | null;
  onEditStory?: () => void;
}

const commentFormSchema = insertCommentSchema.omit({
  storyId: true,
  userId: true,
});

type CommentFormData = z.infer<typeof commentFormSchema>;

export function CommentsSidebar({ isOpen, onClose, storyId, onEditStory }: CommentsSidebarProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<CommentFormData>({
    resolver: zodResolver(commentFormSchema),
    defaultValues: {
      commentText: "",
    },
  });

  const { data: story } = useQuery<StoryWithAssignee>({
    queryKey: ["/api/stories", storyId],
    enabled: !!storyId,
  });

  const { data: comments, isLoading } = useQuery<CommentWithUser[]>({
    queryKey: ["/api/stories", storyId, "comments"],
    enabled: !!storyId,
  });

  const createCommentMutation = useMutation({
    mutationFn: async (data: CommentFormData) => {
      if (!storyId) throw new Error("No story selected");
      const res = await apiRequest("POST", `/api/stories/${storyId}/comments`, data);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/stories", storyId, "comments"] });
      queryClient.invalidateQueries({ queryKey: ["/api/stories"] });
      form.reset();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to post comment",
        variant: "destructive",
      });
    },
  });

  const deleteCommentMutation = useMutation({
    mutationFn: async (commentId: string) => {
      await apiRequest("DELETE", `/api/comments/${commentId}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/stories", storyId, "comments"] });
      queryClient.invalidateQueries({ queryKey: ["/api/stories"] });
      toast({
        title: "Success",
        description: "Comment deleted successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete comment",
        variant: "destructive",
      });
    },
  });

  const getUserInitials = (name: string) => {
    return name
      .split(" ")
      .map(n => n[0])
      .join("")
      .toUpperCase()
      .slice(0, 2);
  };

  const onSubmit = (data: CommentFormData) => {
    createCommentMutation.mutate(data);
  };

  const handleDeleteComment = (commentId: string) => {
    if (window.confirm("Are you sure you want to delete this comment?")) {
      deleteCommentMutation.mutate(commentId);
    }
  };

  const processCommentText = (text: string) => {
    // Simple @mention highlighting
    return text.replace(/@(\w+)/g, '<span class="text-primary font-medium">@$1</span>');
  };

  return (
    <>
      {/* Overlay */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black/50 z-40"
          onClick={onClose}
        />
      )}

      {/* Sidebar */}
      <div
        className={cn(
          "fixed right-0 top-0 h-full w-80 bg-card border-l border-border z-50",
          "transform transition-transform duration-300 ease-in-out",
          isOpen ? "translate-x-0" : "translate-x-full"
        )}
      >
        <div className="flex flex-col h-full">
          {/* Header */}
          <div className="p-6 border-b border-border">
            <div className="flex items-center justify-between">
              <h3 className="text-lg font-semibold text-foreground">Comments</h3>
              <div className="flex items-center space-x-2">
                {onEditStory && (
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={onEditStory}
                    data-testid="button-edit-story"
                  >
                    <Edit className="w-4 h-4 mr-1" />
                    Edit
                  </Button>
                )}
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={onClose}
                  data-testid="button-close-sidebar"
                >
                  <X className="w-4 h-4" />
                </Button>
              </div>
            </div>
            {story && (
              <p className="text-sm text-muted-foreground mt-1" data-testid="text-story-title">
                {story.title}
              </p>
            )}
          </div>

          {/* Comments List */}
          <div className="flex-1 overflow-y-auto p-6">
            {isLoading ? (
              <div className="space-y-4">
                {[...Array(3)].map((_, i) => (
                  <div key={i} className="flex space-x-3">
                    <Skeleton className="w-8 h-8 rounded-full" />
                    <div className="flex-1 space-y-2">
                      <Skeleton className="h-4 w-24" />
                      <Skeleton className="h-12 w-full" />
                    </div>
                  </div>
                ))}
              </div>
            ) : comments && comments.length > 0 ? (
              <div className="space-y-4">
                {comments.map((comment) => (
                  <div
                    key={comment.id}
                    className="bg-muted/50 rounded-lg p-4"
                    data-testid={`comment-${comment.id}`}
                  >
                    <div className="flex items-start space-x-3">
                      <Avatar className="w-8 h-8 flex-shrink-0">
                        <AvatarFallback className="bg-primary text-primary-foreground text-sm">
                          {getUserInitials(comment.user.name)}
                        </AvatarFallback>
                      </Avatar>
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center justify-between mb-1">
                          <div className="flex items-center space-x-2">
                            <span className="text-sm font-medium text-foreground">
                              {comment.user.name}
                            </span>
                            <span className="text-xs text-muted-foreground">
                              {formatDistanceToNow(new Date(comment.createdAt || 0), { addSuffix: true })}
                            </span>
                          </div>
                          {(user?.id === comment.userId || user?.role === "Admin") && (
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => handleDeleteComment(comment.id)}
                              className="w-6 h-6 p-0 text-muted-foreground hover:text-destructive"
                              data-testid={`button-delete-comment-${comment.id}`}
                            >
                              <Trash2 className="w-3 h-3" />
                            </Button>
                          )}
                        </div>
                        <div
                          className="text-sm text-foreground"
                          dangerouslySetInnerHTML={{
                            __html: processCommentText(comment.commentText),
                          }}
                        />
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center text-muted-foreground py-8">
                <p>No comments yet</p>
                <p className="text-xs mt-1">Be the first to comment on this story</p>
              </div>
            )}
          </div>

          {/* Comment Input */}
          <div className="p-6 border-t border-border">
            <form onSubmit={form.handleSubmit(onSubmit)} className="flex space-x-2">
              <Input
                {...form.register("commentText")}
                placeholder="Add a comment... (use @username to mention)"
                className="flex-1"
                disabled={createCommentMutation.isPending}
                data-testid="input-comment"
              />
              <Button
                type="submit"
                size="sm"
                disabled={createCommentMutation.isPending || !form.watch("commentText")?.trim()}
                data-testid="button-post-comment"
              >
                <Send className="w-4 h-4" />
              </Button>
            </form>
            {form.formState.errors.commentText && (
              <p className="text-sm text-destructive mt-1">
                {form.formState.errors.commentText.message}
              </p>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

client/src/components/dashboard.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { KanbanBoard } from "@/components/kanban-board";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Skeleton } from "@/components/ui/skeleton";
import { 
  ClipboardList, 
  CheckCircle, 
  Star, 
  Users, 
  ArrowUp, 
  ArrowDown,
  Expand,
  TrendingUp,
  MessageCircle,
  Plus
} from "lucide-react";
import { useEffect, useRef } from "react";

interface DashboardMetrics {
  totalStories: number;
  completed: number;
  totalPoints: number;
  teamMembers: number;
  storiesByStatus: Record<string, number>;
  storiesByPriority: Record<string, number>;
}

export function Dashboard() {
  const chartRefs = {
    completion: useRef<HTMLCanvasElement>(null),
    priority: useRef<HTMLCanvasElement>(null),
  };

  const { data: metrics, isLoading } = useQuery<DashboardMetrics>({
    queryKey: ["/api/dashboard/metrics"],
  });

  // Initialize charts when data is available
  useEffect(() => {
    if (!metrics || !(window as any).Chart) return;

    // Completion Trend Chart
    const completionCtx = chartRefs.completion.current;
    if (completionCtx) {
      const completionChart = new (window as any).Chart(completionCtx, {
        type: 'line',
        data: {
          labels: ['Week 1', 'Week 2', 'Week 3', 'Week 4'],
          datasets: [{
            label: 'Completed Stories',
            data: [4, 7, 12, metrics.completed],
            borderColor: 'hsl(173 58% 39%)',
            backgroundColor: 'hsl(173 58% 39% / 0.1)',
            tension: 0.4,
            fill: true,
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          plugins: {
            legend: {
              display: false
            }
          },
          scales: {
            y: {
              beginAtZero: true,
              grid: {
                color: 'hsl(214.3 31.8% 91.4%)',
              },
              ticks: {
                color: 'hsl(215.4 16.3% 46.9%)',
              }
            },
            x: {
              grid: {
                color: 'hsl(214.3 31.8% 91.4%)',
              },
              ticks: {
                color: 'hsl(215.4 16.3% 46.9%)',
              }
            }
          }
        }
      });

      return () => completionChart.destroy();
    }
  }, [metrics]);

  useEffect(() => {
    if (!metrics || !(window as any).Chart) return;

    // Priority Distribution Chart
    const priorityCtx = chartRefs.priority.current;
    if (priorityCtx) {
      const priorityChart = new (window as any).Chart(priorityCtx, {
        type: 'doughnut',
        data: {
          labels: ['High', 'Medium', 'Low'],
          datasets: [{
            data: [
              metrics.storiesByPriority.High || 0,
              metrics.storiesByPriority.Medium || 0,
              metrics.storiesByPriority.Low || 0,
            ],
            backgroundColor: [
              'hsl(0 84.2% 60.2%)',
              'hsl(43 74% 66%)',
              'hsl(197 37% 24%)'
            ],
            borderWidth: 2,
            borderColor: 'hsl(0 0% 100%)',
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          plugins: {
            legend: {
              position: 'bottom',
              labels: {
                color: 'hsl(215.4 16.3% 46.9%)',
                padding: 20,
              }
            }
          }
        }
      });

      return () => priorityChart.destroy();
    }
  }, [metrics]);

  const MetricCard = ({ 
    title, 
    value, 
    icon: Icon, 
    change, 
    changeType 
  }: { 
    title: string;
    value: string | number;
    icon: React.ElementType;
    change: string;
    changeType: 'up' | 'down';
  }) => (
    <Card>
      <CardContent className="p-6">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm text-muted-foreground">{title}</p>
            <p className="text-2xl font-bold text-foreground" data-testid={`metric-${title.toLowerCase().replace(' ', '-')}`}>
              {value}
            </p>
          </div>
          <div className="w-12 h-12 bg-primary/10 rounded-lg flex items-center justify-center">
            <Icon className="w-6 h-6 text-primary" />
          </div>
        </div>
        <div className="mt-4 flex items-center space-x-1">
          {changeType === 'up' ? (
            <ArrowUp className="w-4 h-4 text-green-500" />
          ) : (
            <ArrowDown className="w-4 h-4 text-red-500" />
          )}
          <span className={`text-sm ${changeType === 'up' ? 'text-green-500' : 'text-red-500'}`}>
            {change}
          </span>
        </div>
      </CardContent>
    </Card>
  );

  if (isLoading) {
    return (
      <div className="flex-1 overflow-y-auto p-6 space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {[...Array(4)].map((_, i) => (
            <Card key={i}>
              <CardContent className="p-6">
                <Skeleton className="h-4 w-24 mb-2" />
                <Skeleton className="h-8 w-16 mb-4" />
                <Skeleton className="h-4 w-32" />
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  if (!metrics) {
    return (
      <div className="flex-1 flex items-center justify-center">
        <p className="text-muted-foreground">Failed to load dashboard metrics</p>
      </div>
    );
  }

  return (
    <div className="flex-1 h-full overflow-auto scrollbar-thin scrollbar-thumb-gray-300 scrollbar-track-transparent">
      <div className="min-w-full p-6 space-y-8">
        {/* Dashboard Header */}
        <div className="flex flex-col space-y-2">
          <h1 className="text-3xl font-bold text-foreground">Project Dashboard</h1>
          <p className="text-muted-foreground">
            Track your team's progress and project metrics
          </p>
        </div>

        {/* Key Metrics Summary */}
        <Card className="bg-gradient-to-r from-primary/10 to-primary/5 border-primary/20">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <TrendingUp className="w-6 h-6 text-primary" />
              <span>Project Summary</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            <div className="text-center">
              <p className="text-3xl font-bold text-primary">{metrics?.totalStories || 0}</p>
              <p className="text-sm text-muted-foreground">Total Stories</p>
              <p className="text-xs text-green-600">+12% from last week</p>
            </div>
            <div className="text-center">
              <p className="text-3xl font-bold text-green-600">{metrics?.completed || 0}</p>
              <p className="text-sm text-muted-foreground">Completed</p>
              <p className="text-xs text-green-600">+8% completion rate</p>
            </div>
            <div className="text-center">
              <p className="text-3xl font-bold text-orange-600">{metrics?.totalPoints || 0}</p>
              <p className="text-sm text-muted-foreground">Story Points</p>
              <p className="text-xs text-red-600">-3% from target</p>
            </div>
            <div className="text-center">
              <p className="text-3xl font-bold text-blue-600">{metrics?.teamMembers || 0}</p>
              <p className="text-sm text-muted-foreground">Team Members</p>
              <p className="text-xs text-green-600">+1 this month</p>
            </div>
          </CardContent>
        </Card>

        {/* Metrics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 min-w-fit">
          <MetricCard
            title="Total Stories"
            value={metrics.totalStories}
            icon={ClipboardList}
            change="+12% from last week"
            changeType="up"
          />
          <MetricCard
            title="Completed"
            value={metrics.completed}
            icon={CheckCircle}
            change="+8% from last week"
            changeType="up"
          />
          <MetricCard
            title="Total Points"
            value={metrics.totalPoints}
            icon={Star}
            change="-3% from last week"
            changeType="down"
          />
          <MetricCard
            title="Team Members"
            value={metrics.teamMembers}
            icon={Users}
            change="+1 this month"
            changeType="up"
          />
        </div>

        {/* Charts Section */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 min-w-fit">
          <Card className="min-w-[400px]">
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <TrendingUp className="w-5 h-5" />
                <span>Story Completion Trend</span>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="h-80 w-full">
                <canvas ref={chartRefs.completion} data-testid="chart-completion"></canvas>
              </div>
            </CardContent>
          </Card>

          <Card className="min-w-[400px]">
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <Star className="w-5 h-5" />
                <span>Priority Distribution</span>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="h-80 w-full">
                <canvas ref={chartRefs.priority} data-testid="chart-priority"></canvas>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Status Distribution Chart */}
        <Card className="w-full">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <ClipboardList className="w-5 h-5" />
              <span>Story Status Overview</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
              {Object.entries(metrics?.storiesByStatus || {}).map(([status, count]) => (
                <div key={status} className="text-center p-4 bg-muted/30 rounded-lg">
                  <div className="text-2xl font-bold text-foreground">{count}</div>
                  <div className="text-sm text-muted-foreground">{status}</div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Kanban Board Section */}
        <Card className="w-full">
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle className="flex items-center space-x-2">
                <div className="w-3 h-3 bg-primary rounded-full"></div>
                <span>Kanban Board Preview</span>
              </CardTitle>
              <div className="flex items-center space-x-2">
                <Select defaultValue="all-sprints">
                  <SelectTrigger className="w-32">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all-sprints">All Sprints</SelectItem>
                    <SelectItem value="sprint-1">Sprint 1</SelectItem>
                    <SelectItem value="sprint-2">Sprint 2</SelectItem>
                  </SelectContent>
                </Select>
                <Button variant="secondary" size="sm" data-testid="button-full-kanban">
                  <Expand className="w-4 h-4 mr-1" />
                  Full View
                </Button>
              </div>
            </div>
          </CardHeader>
          <CardContent className="p-6">
            <div className="overflow-x-auto">
              <KanbanBoard compact />
            </div>
          </CardContent>
        </Card>

        {/* Recent Activity */}
        <Card className="w-full">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <MessageCircle className="w-5 h-5" />
              <span>Recent Activity</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="flex items-start space-x-3" data-testid="activity-item-1">
                <div className="w-8 h-8 bg-green-500 rounded-full flex items-center justify-center flex-shrink-0">
                  <CheckCircle className="w-4 h-4 text-white" />
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm text-foreground">
                    <span className="font-medium">Admin User</span> completed story{" "}
                    <span className="font-medium">"Project Setup"</span>
                  </p>
                  <p className="text-xs text-muted-foreground">2 hours ago</p>
                </div>
              </div>

              <div className="flex items-start space-x-3" data-testid="activity-item-2">
                <div className="w-8 h-8 bg-primary rounded-full flex items-center justify-center flex-shrink-0">
                  <MessageCircle className="w-4 h-4 text-white" />
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm text-foreground">
                    <span className="font-medium">Team Lead</span> commented on{" "}
                    <span className="font-medium">"Setup Authentication System"</span>
                  </p>
                  <p className="text-xs text-muted-foreground">4 hours ago</p>
                </div>
              </div>

              <div className="flex items-start space-x-3" data-testid="activity-item-3">
                <div className="w-8 h-8 bg-orange-500 rounded-full flex items-center justify-center flex-shrink-0">
                  <Plus className="w-4 h-4 text-white" />
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm text-foreground">
                    <span className="font-medium">Regular User</span> created new story{" "}
                    <span className="font-medium">"Implement AI Estimation"</span>
                  </p>
                  <p className="text-xs text-muted-foreground">1 day ago</p>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Load Chart.js */}
        <script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
      </div>
    </div>
  );
}

