import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { DragDropContext, Droppable, Draggable, DropResult } from "react-beautiful-dnd";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useForm } from "react-hook-form";
import {
  MessageCircle,
  Plus,
  Star,
  Settings,
  Paperclip,
  Users,
  ArrowUpDown,
  List,
  Grid3x3,
  Activity,
  Calendar,
  Hash,
  Filter,
  SortAsc,
  SortDesc,
  Search
} from "lucide-react";
import { StoryWithAssignee } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/use-auth";
import { cn } from "@/lib/utils";

interface KanbanBoardProps {
  compact?: boolean;
  onStoryClick?: (storyId: string) => void;
  onCreateStory?: () => void;
}

const COLUMN_NAMES = ["To Do", "In Progress", "Blocked", "Validation", "Completed"];

const COLUMN_COLORS = {
  "To Do": "hsl(12 76% 61%)",
  "In Progress": "hsl(173 58% 39%)",
  "Blocked": "hsl(0 84.2% 60.2%)",
  "Validation": "hsl(43 74% 66%)",
  "Completed": "hsl(142 76% 36%)",
};

const PRIORITY_COLORS = {
  High: "hsl(0 84.2% 60.2%)",
  Medium: "hsl(43 74% 66%)",
  Low: "hsl(142 76% 36%)",
};

export function KanbanBoard({ compact = false, onStoryClick, onCreateStory }: KanbanBoardProps) {
  const [columns, setColumns] = useState<Record<string, StoryWithAssignee[]>>({});
  const [selectedProject, setSelectedProject] = useState("all");
  const [selectedTeamLead, setSelectedTeamLead] = useState("all");
  const [viewMode, setViewMode] = useState<"kanban" | "list">("kanban");

  // List view filters and sorting
  const [sortColumn, setSortColumn] = useState<string | null>(null);
  const [sortDirection, setSortDirection] = useState<"asc" | "desc">("asc");
  const [filters, setFilters] = useState({
    story: "",
    title: "",
    assignee: "",
    status: "all",
    priority: "all",
    points: "",
    type: "all"
  });
  const [viewDensity, setViewDensity] = useState<"compact" | "normal" | "comfortable">("normal");

  const queryClient = useQueryClient();
  const { user } = useAuth();

  const { data: stories, isLoading } = useQuery<StoryWithAssignee[]>({
    queryKey: ["/api/stories"],
  });

  const { data: users } = useQuery({
    queryKey: ["/api/users"],
  });

  const updateStoryMutation = useMutation({
    mutationFn: async ({ storyId, updates }: { storyId: string; updates: any }) => {
      const res = await apiRequest("PATCH", `/api/stories/${storyId}`, updates);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/stories"] });
    },
  });

  const createStoryMutation = useMutation({
    mutationFn: async (columnStatus: string) => {
      if (!user?.id) {
        throw new Error("User authentication required");
      }

      const newStory = {
        title: "New Story",
        description: "Click to edit description",
        status: columnStatus,
        priority: "Medium" as const,
        storyType: "Story" as const,
        pointer: 1,
        project: selectedProject === "all" ? "T&D" : selectedProject,
        acceptanceCriteria: "â€¢ Add acceptance criteria here",
        createdBy: user.id,
        teamLead: selectedTeamLead === "all" ? undefined : selectedTeamLead,
      };
      const res = await apiRequest("POST", "/api/stories", newStory);
      return await res.json();
    },
    onSuccess: (newStory) => {
      queryClient.invalidateQueries({ queryKey: ["/api/stories"] });
      // Optionally, you can open the story for editing immediately
      setTimeout(() => {
        onStoryClick?.(newStory.id);
      }, 500);
    },
    onError: (error: Error) => {
      console.error("Failed to create story:", error);
    },
  });

  // Organize stories by status
  useEffect(() => {
    if (stories) {
      const organized = COLUMN_NAMES.reduce((acc, status) => {
        acc[status] = stories.filter(story => story.status === status);
        return acc;
      }, {} as Record<string, StoryWithAssignee[]>);
      setColumns(organized);
    }
  }, [stories]);

  const onDragEnd = (result: DropResult) => {
    const { source, destination, draggableId } = result;

    if (!destination) return;
    if (source.droppableId === destination.droppableId && source.index === destination.index) return;

    const startColumn = [...columns[source.droppableId]];
    const endColumn = source.droppableId === destination.droppableId
      ? startColumn
      : [...columns[destination.droppableId]];

    // Remove from source
    const [removed] = startColumn.splice(source.index, 1);

    // Add to destination
    if (source.droppableId === destination.droppableId) {
      startColumn.splice(destination.index, 0, removed);
    } else {
      endColumn.splice(destination.index, 0, removed);
    }

    // Update local state immediately for smooth UX
    const newColumns = {
      ...columns,
      [source.droppableId]: startColumn,
      [destination.droppableId]: endColumn,
    };
    setColumns(newColumns);

    // Update story status on backend
    updateStoryMutation.mutate({
      storyId: draggableId,
      updates: { status: destination.droppableId },
    });
  };

  const getUserInitials = (name: string) => {
    return name
      .split(" ")
      .map(n => n[0])
      .join("")
      .toUpperCase()
      .slice(0, 2);
  };

  const getStoryNumber = (story: StoryWithAssignee) => {
    // Use the story number from database or fallback to generated one
    return story.storyNumber || `${story.project || "T&D"}-0000`;
  };

  // Filter and sort stories for list view
  const getFilteredAndSortedStories = () => {
    if (!stories) return [];

    let filtered = stories.filter(story => {
      const matchesProject = selectedProject === "all" || story.project === selectedProject;
      const matchesTeamLead = selectedTeamLead === "all" || story.teamLead === selectedTeamLead;
      const matchesStory = filters.story === "" || getStoryNumber(story).toLowerCase().includes(filters.story.toLowerCase());
      const matchesTitle = filters.title === "" || story.title.toLowerCase().includes(filters.title.toLowerCase());
      const matchesAssignee = filters.assignee === "" || (story.assignee?.name.toLowerCase().includes(filters.assignee.toLowerCase()) ?? false);
      const matchesStatus = filters.status === "all" || story.status === filters.status;
      const matchesPriority = filters.priority === "all" || story.priority === filters.priority;
      const matchesPoints = filters.points === "" || story.pointer?.toString() === filters.points;
      const matchesType = filters.type === "all" || story.storyType === filters.type;

      return matchesProject && matchesTeamLead && matchesStory && matchesTitle && matchesAssignee && matchesStatus && matchesPriority && matchesPoints && matchesType;
    });

    if (sortColumn) {
      filtered.sort((a, b) => {
        let aVal: any, bVal: any;

        switch (sortColumn) {
          case "story":
            aVal = getStoryNumber(a);
            bVal = getStoryNumber(b);
            break;
          case "title":
            aVal = a.title;
            bVal = b.title;
            break;
          case "assignee":
            aVal = a.assignee?.name || "";
            bVal = b.assignee?.name || "";
            break;
          case "status":
            aVal = a.status;
            bVal = b.status;
            break;
          case "priority":
            const priorityOrder = { "Low": 0, "Medium": 1, "High": 2 };
            aVal = priorityOrder[a.priority as keyof typeof priorityOrder];
            bVal = priorityOrder[b.priority as keyof typeof priorityOrder];
            break;
          case "points":
            aVal = a.pointer || 0;
            bVal = b.pointer || 0;
            break;
          case "type":
            aVal = a.storyType;
            bVal = b.storyType;
            break;
          default:
            return 0;
        }

        if (aVal < bVal) return sortDirection === "asc" ? -1 : 1;
        if (aVal > bVal) return sortDirection === "asc" ? 1 : -1;
        return 0;
      });
    }

    return filtered;
  };

  const handleSort = (column: string) => {
    if (sortColumn === column) {
      setSortDirection(sortDirection === "asc" ? "desc" : "asc");
    } else {
      setSortColumn(column);
      setSortDirection("asc");
    }
  };

  const handleFilterChange = (key: string, value: string) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  };

  const canManageBoard = user?.role === "Admin" || user?.role === "Team Lead";

  const handleCreateNewTask = (columnStatus: string) => {
    createStoryMutation.mutate(columnStatus);
  };

  const StoryCard = ({ story, index }: { story: StoryWithAssignee; index: number }) => {
    const getCardScale = () => {
      switch (viewDensity) {
        case "compact": return "scale-90";
        case "comfortable": return "scale-110";
        default: return "scale-100";
      }
    };

    return (
      <Draggable draggableId={story.id} index={index}>
        {(provided, snapshot) => (
          <Card
            ref={provided.innerRef}
            {...provided.draggableProps}
            {...provided.dragHandleProps}
            className={cn(
              "mb-3 transition-all duration-200 hover:shadow-md border border-border/50 transform-gpu",
              snapshot.isDragging && "rotate-1 shadow-lg border-primary/50 scale-105",
              getCardScale(),
              viewDensity === "compact" && "mb-2",
              viewDensity === "comfortable" && "mb-4"
            )}
            data-testid={`story-card-${story.id}`}
            style={{
              ...provided.draggableProps.style,
              transformOrigin: "center"
            }}
          >
            <CardContent
              className={cn(
                "hover:bg-muted/30 transition-colors cursor-pointer",
                viewDensity === "compact" && "p-2 text-sm",
                viewDensity === "comfortable" && "p-6 text-base",
                viewDensity === "normal" && "p-4"
              )}
              onClick={(e) => {
                e.stopPropagation();
                onStoryClick?.(story.id);
              }}
            >
              {/* Story Number at the top */}
              <div className="flex items-center justify-between mb-2">
                <Badge variant="outline" className="text-xs font-mono">
                  <Hash className="w-3 h-3 mr-1" />
                  {getStoryNumber(story)}
                </Badge>
                <Badge
                  variant="secondary"
                  className="text-xs px-2 py-1 flex-shrink-0"
                  style={{
                    backgroundColor: `${PRIORITY_COLORS[story.priority as keyof typeof PRIORITY_COLORS]}20`,
                    color: PRIORITY_COLORS[story.priority as keyof typeof PRIORITY_COLORS],
                  }}
                >
                  {story.priority}
                </Badge>
              </div>

              {/* Story Type */}
              {story.storyType && (
                <Badge variant="outline" className="text-xs px-2 py-0.5 mb-2">
                  {story.storyType}
                </Badge>
              )}

              {/* Title */}
              <h5 className={cn(
                "font-medium text-foreground leading-tight hover:text-primary transition-colors",
                viewDensity === "compact" && "text-sm",
                viewDensity === "normal" && "text-base",
                viewDensity === "comfortable" && "text-lg"
              )}>
                {story.title}
              </h5>

              {/* Description (only in normal/comfortable view) */}
              {story.description && (
                <p className={cn(
                  "text-muted-foreground mb-3 line-clamp-2",
                  viewDensity === "compact" && "text-xs",
                  viewDensity === "normal" && "text-sm",
                  viewDensity === "comfortable" && "text-base"
                )}>
                  {story.description}
                </p>
              )}

              {/* Bottom Row - Story Points, Assignee, Attachments, Comments */}
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-2">
                  {story.pointer && (
                    <div className="flex items-center space-x-1">
                      <Star className="w-3 h-3 text-amber-500 fill-amber-500" />
                      <span className={cn("text-muted-foreground", viewDensity === "compact" ? "text-xs" : "text-sm")}>{story.pointer}</span>
                    </div>
                  )}
                  <div className="flex items-center space-x-1">
                    <Paperclip className={cn("text-muted-foreground", viewDensity === "compact" ? "w-3 h-3" : "w-4 h-4")} />
                    <span className={cn("text-muted-foreground", viewDensity === "compact" ? "text-xs" : "text-sm")}>0</span>
                  </div>
                </div>

                <div className="flex items-center space-x-2">
                  {/* Assigned User */}
                  {story.assignee && (
                    <div className="flex items-center space-x-1">
                      <Avatar className={cn(viewDensity === "compact" ? "w-5 h-5" : "w-7 h-7")}>
                        <AvatarFallback className={cn(viewDensity === "compact" ? "text-xs" : "text-sm")}>
                          {getUserInitials(story.assignee.name)}
                        </AvatarFallback>
                      </Avatar>
                      {(
                        <span className={cn("text-muted-foreground", viewDensity === "compact" ? "text-xs" : "text-sm")}>{story.assignee.name}</span>
                      )}
                    </div>
                  )}
                  <div className="flex items-center space-x-1">
                    <MessageCircle className={cn("text-muted-foreground", viewDensity === "compact" ? "w-3 h-3" : "w-4 h-4")} />
                    <span className={cn("text-muted-foreground", viewDensity === "compact" ? "text-xs" : "text-sm")}>
                      {story.commentCount || 0}
                    </span>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        )}
      </Draggable>
    );
  };

  if (isLoading) {
    return (
      <div className="h-full flex flex-col">
        <div className="flex-1 p-6">
          <div className="flex space-x-6 overflow-x-auto">
            {COLUMN_NAMES.map((columnName) => (
              <div key={columnName} className="flex-shrink-0 w-80">
                <div className="bg-muted/50 rounded-lg p-4">
                  <Skeleton className="h-6 w-32 mb-4" />
                  <div className="space-y-3">
                    {[...Array(2)].map((_, i) => (
                      <Skeleton key={i} className="h-32 w-full" />
                    ))}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col bg-background">
      {/* Header Section */}
      <div className="border-b border-border bg-card">
        <div className="p-4">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-4">
              {/* Project Dropdown */}
              <Select value={selectedProject} onValueChange={(value) => {
                setSelectedProject(value);
                // Auto-sync team lead based on project
                if (value === "T&D") {
                  setSelectedTeamLead("Shantnu");
                } else if (value === "ADMS") {
                  setSelectedTeamLead("Pranav");
                } else {
                  setSelectedTeamLead("all");
                }
              }}>
                <SelectTrigger className="w-40">
                  <SelectValue placeholder="Select Project" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Projects</SelectItem>
                  <SelectItem value="T&D">T&D</SelectItem>
                  <SelectItem value="ADMS">ADMS</SelectItem>
                </SelectContent>
              </Select>

              {/* Team Lead Dropdown */}
              <Select value={selectedTeamLead} onValueChange={setSelectedTeamLead}>
                <SelectTrigger className="w-40">
                  <SelectValue placeholder="Select Team Lead" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Team Leads</SelectItem>
                  {(selectedProject === "all" || selectedProject === "T&D") && (
                    <SelectItem value="Shantnu">Shantnu</SelectItem>
                  )}
                  {(selectedProject === "all" || selectedProject === "ADMS") && (
                    <SelectItem value="Pranav">Pranav</SelectItem>
                  )}
                </SelectContent>
              </Select>
            </div>

          </div>
        </div>

        {/* Board-Level Toolbar - Available for all roles */}
        <div className="border-t border-border px-4 py-2 bg-muted/30">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              {canManageBoard && (
                <Button
                  size="sm"
                  className="flex items-center space-x-2"
                  onClick={() => handleCreateNewTask("To Do")}
                  data-testid="button-create-new-story"
                >
                  <Plus className="w-4 h-4" />
                  <span>+ New Story</span>
                </Button>
              )}

              {/* View Mode Switch - Available for all roles */}
              <Button
                variant="outline"
                size="sm"
                onClick={() => setViewMode(viewMode === "kanban" ? "list" : "kanban")}
              >
                {viewMode === "kanban" ? <List className="w-4 h-4 mr-2" /> : <Grid3x3 className="w-4 h-4 mr-2" />}
                {viewMode === "kanban" ? "List View" : "Board View"}
              </Button>
            </div>

            <div className="flex items-center space-x-2">

              {canManageBoard && (
                <Button variant="outline" size="sm">
                  <Settings className="w-4 h-4 mr-2" />
                  Board Settings
                </Button>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Main Board Area */}
      <div className="flex-1 overflow-hidden">
        {viewMode === "kanban" ? (
          <DragDropContext onDragEnd={onDragEnd}>
            <div className="h-full overflow-auto scrollbar-thin">
              <div className="flex space-x-6 p-6 min-w-max">
                {COLUMN_NAMES.map((columnName) => (
                  <Droppable droppableId={columnName} key={columnName}>
                    {(provided, snapshot) => (
                      <div
                        {...provided.droppableProps}
                        ref={provided.innerRef}
                        className={cn(
                          "flex-shrink-0",
                          "w-80"
                        )}
                      >
                        <div className={cn(
                          "bg-muted/50 rounded-lg border border-border/50 h-full",
                          snapshot.isDraggingOver && "bg-muted/70 border-primary/50"
                        )}>
                          {/* Column Header */}
                          <div className="p-4 border-b border-border/50 bg-card/50 rounded-t-lg">
                            <div className="flex items-center justify-between mb-2">
                              <div className="flex items-center space-x-2">
                                <div
                                  className="w-3 h-3 rounded-full"
                                  style={{ backgroundColor: COLUMN_COLORS[columnName as keyof typeof COLUMN_COLORS] }}
                                />
                                <h4 className="font-medium text-foreground">
                                  {columnName}
                                </h4>
                                <Badge variant="secondary" className="text-xs px-2 py-1">
                                  {columns[columnName]?.length || 0}
                                </Badge>
                              </div>
                              <div className="flex items-center space-x-1">
                                {canManageBoard && (
                                  <Button variant="ghost" size="sm" className="w-6 h-6 p-0">
                                    <Settings className="w-3 h-3" />
                                  </Button>
                                )}
                              </div>
                            </div>
                            {/* WIP Limit Indicator */}
                            {canManageBoard && (
                              <div className="text-xs text-muted-foreground">
                                WIP Limit: {columns[columnName]?.length || 0}/5
                              </div>
                            )}
                          </div>

                          {/* Column Content */}
                          <div className="p-4 space-y-3 min-h-[500px] max-h-[calc(100vh-300px)] overflow-y-auto scrollbar-thin">
                            {columns[columnName]?.map((story, index) => (
                              <StoryCard key={story.id} story={story} index={index} />
                            ))}
                            {provided.placeholder}
                          </div>
                        </div>
                      </div>
                    )}
                  </Droppable>
                ))}
              </div>
            </div>
          </DragDropContext>
        ) : (
          // List View
          <div className="h-full overflow-auto p-6">
            <div className="bg-card rounded-lg border">
              {/* Filters Row */}
              <div className="grid grid-cols-12 gap-4 p-4 bg-muted/20 border-b">
                <div className="col-span-1">
                  <Input
                    placeholder="Story #..."
                    value={filters.story}
                    onChange={(e) => handleFilterChange("story", e.target.value)}
                    className="h-8 text-xs"
                  />
                </div>
                <div className="col-span-3">
                  <Input
                    placeholder="Title..."
                    value={filters.title}
                    onChange={(e) => handleFilterChange("title", e.target.value)}
                    className="h-8 text-xs"
                  />
                </div>
                <div className="col-span-2">
                  <Select value={filters.assignee} onValueChange={(value) => handleFilterChange("assignee", value)}>
                    <SelectTrigger className="h-8 text-xs">
                      <SelectValue placeholder="Assignee" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All</SelectItem>
                      <SelectItem value="unassigned">Unassigned</SelectItem>
                      {users && Array.isArray(users) ? users.map((user: any) => (
                        <SelectItem key={user.id} value={user.name}>
                          {user.name}
                        </SelectItem>
                      )) : null}
                    </SelectContent>
                  </Select>
                </div>
                <div className="col-span-2">
                  <Select value={filters.status} onValueChange={(value) => handleFilterChange("status", value)}>
                    <SelectTrigger className="h-8 text-xs">
                      <SelectValue placeholder="Status" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All</SelectItem>
                      <SelectItem value="To Do">To Do</SelectItem>
                      <SelectItem value="In Progress">In Progress</SelectItem>
                      <SelectItem value="Blocked">Blocked</SelectItem>
                      <SelectItem value="Validation">Validation</SelectItem>
                      <SelectItem value="Completed">Completed</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div className="col-span-1">
                  <Select value={filters.priority} onValueChange={(value) => handleFilterChange("priority", value)}>
                    <SelectTrigger className="h-8 text-xs">
                      <SelectValue placeholder="Priority" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All</SelectItem>
                      <SelectItem value="Low">Low</SelectItem>
                      <SelectItem value="Medium">Medium</SelectItem>
                      <SelectItem value="High">High</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div className="col-span-1">
                  <Input
                    placeholder="Points..."
                    value={filters.points}
                    onChange={(e) => handleFilterChange("points", e.target.value)}
                    className="h-8 text-xs"
                  />
                </div>
                <div className="col-span-1">
                  <Select value={filters.type} onValueChange={(value) => handleFilterChange("type", value)}>
                    <SelectTrigger className="h-8 text-xs">
                      <SelectValue placeholder="Type" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All</SelectItem>
                      <SelectItem value="Story">Story</SelectItem>
                      <SelectItem value="Bug">Bug</SelectItem>
                      <SelectItem value="Epic">Epic</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div className="col-span-1">
                  <div className="h-8"></div> {/* Actions column - no filter */}
                </div>
              </div>

              {/* Table Header with Sorting */}
              <div className="grid grid-cols-12 gap-4 p-4 bg-muted/50 border-b text-sm font-medium text-muted-foreground">
                <div className="col-span-1 flex items-center cursor-pointer" onClick={() => handleSort("story")}>
                  Story #
                  {sortColumn === "story" && (
                    sortDirection === "asc" ? <SortAsc className="w-3 h-3 ml-1" /> : <SortDesc className="w-3 h-3 ml-1" />
                  )}
                </div>
                <div className="col-span-3 flex items-center cursor-pointer" onClick={() => handleSort("title")}>
                  Title
                  {sortColumn === "title" && (
                    sortDirection === "asc" ? <SortAsc className="w-3 h-3 ml-1" /> : <SortDesc className="w-3 h-3 ml-1" />
                  )}
                </div>
                <div className="col-span-2 flex items-center cursor-pointer" onClick={() => handleSort("assignee")}>
                  Assignee
                  {sortColumn === "assignee" && (
                    sortDirection === "asc" ? <SortAsc className="w-3 h-3 ml-1" /> : <SortDesc className="w-3 h-3 ml-1" />
                  )}
                </div>
                <div className="col-span-2 flex items-center cursor-pointer" onClick={() => handleSort("status")}>
                  Status
                  {sortColumn === "status" && (
                    sortDirection === "asc" ? <SortAsc className="w-3 h-3 ml-1" /> : <SortDesc className="w-3 h-3 ml-1" />
                  )}
                </div>
                <div className="col-span-1 flex items-center cursor-pointer" onClick={() => handleSort("priority")}>
                  Priority
                  {sortColumn === "priority" && (
                    sortDirection === "asc" ? <SortAsc className="w-3 h-3 ml-1" /> : <SortDesc className="w-3 h-3 ml-1" />
                  )}
                </div>
                <div className="col-span-1 flex items-center cursor-pointer" onClick={() => handleSort("points")}>
                  Points
                  {sortColumn === "points" && (
                    sortDirection === "asc" ? <SortAsc className="w-3 h-3 ml-1" /> : <SortDesc className="w-3 h-3 ml-1" />
                  )}
                </div>
                <div className="col-span-1 flex items-center cursor-pointer" onClick={() => handleSort("type")}>
                  Type
                  {sortColumn === "type" && (
                    sortDirection === "asc" ? <SortAsc className="w-3 h-3 ml-1" /> : <SortDesc className="w-3 h-3 ml-1" />
                  )}
                </div>
                <div className="col-span-1">Actions</div>
              </div>

              {/* Table Body */}
              <div className="divide-y divide-border">
                {getFilteredAndSortedStories().length === 0 ? (
                  <div className="p-8 text-center text-muted-foreground">
                    <Search className="w-12 h-12 mx-auto mb-4 opacity-50" />
                    <p>No stories found matching your filters</p>
                  </div>
                ) : (
                  getFilteredAndSortedStories().map((story) => (
                    <div
                      key={story.id}
                      className="grid grid-cols-12 gap-4 p-4 hover:bg-muted/50 cursor-pointer transition-colors"
                      onClick={() => onStoryClick?.(story.id)}
                    >
                      <div className="col-span-1">
                        <Badge variant="outline" className="text-xs font-mono">
                          <Hash className="w-3 h-3 mr-1" />
                          {getStoryNumber(story)}
                        </Badge>
                      </div>
                      <div className="col-span-3">
                        <div className="font-medium text-sm">{story.title}</div>
                        {story.description && (
                          <div className="text-xs text-muted-foreground mt-1 line-clamp-1">
                            {story.description}
                          </div>
                        )}
                      </div>
                      <div className="col-span-2">
                        {story.assignee ? (
                          <div className="flex items-center space-x-2">
                            <Avatar className="w-6 h-6">
                              <AvatarFallback className="text-xs">
                                {getUserInitials(story.assignee.name)}
                              </AvatarFallback>
                            </Avatar>
                            <span className="text-sm">{story.assignee.name}</span>
                          </div>
                        ) : (
                          <span className="text-sm text-muted-foreground">Unassigned</span>
                        )}
                      </div>
                      <div className="col-span-2">
                        <Badge
                          variant="outline"
                          className="text-xs"
                          style={{
                            backgroundColor: `${COLUMN_COLORS[story.status as keyof typeof COLUMN_COLORS]}20`,
                            borderColor: COLUMN_COLORS[story.status as keyof typeof COLUMN_COLORS],
                            color: COLUMN_COLORS[story.status as keyof typeof COLUMN_COLORS],
                          }}
                        >
                          {story.status}
                        </Badge>
                      </div>
                      <div className="col-span-1">
                        <Badge
                          variant="secondary"
                          className="text-xs"
                          style={{
                            backgroundColor: `${PRIORITY_COLORS[story.priority as keyof typeof PRIORITY_COLORS]}20`,
                            color: PRIORITY_COLORS[story.priority as keyof typeof PRIORITY_COLORS],
                          }}
                        >
                          {story.priority}
                        </Badge>
                      </div>
                      <div className="col-span-1">
                        <div className="flex items-center space-x-1">
                          <Star className="w-3 h-3 text-amber-500 fill-amber-500" />
                          <span className="text-sm">{story.pointer}</span>
                        </div>
                      </div>
                      <div className="col-span-1">
                        <Badge variant="outline" className="text-xs">
                          {story.storyType}
                        </Badge>
                      </div>
                      <div className="col-span-1">
                        <div className="flex items-center space-x-2">
                          <div className="flex items-center space-x-1">
                            <MessageCircle className="w-3 h-3 text-muted-foreground" />
                            <span className="text-xs">{story.commentCount || 0}</span>
                          </div>
                          <div className="flex items-center space-x-1">
                            <Paperclip className="w-3 h-3 text-muted-foreground" />
                            <span className="text-xs">0</span>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Compact Footer */}
      <div className="border-t border-border bg-card p-2">
        <div className="flex items-center justify-between text-sm text-muted-foreground">
          <div className="flex items-center space-x-4">
            <span>Total: {stories?.length || 0}</span>
            <span>Done: {columns["Completed"]?.length || 0}</span>
            <span>Active: {columns["In Progress"]?.length || 0}</span>
          </div>
          <div className="flex items-center space-x-2">
            <Activity className="w-3 h-3" />
            <span>Last updated: {new Date().toLocaleTimeString()}</span>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/sidebar.tsx.content:
import { useAuth } from "@/hooks/use-auth";
import { useTheme } from "@/lib/theme-context";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { 
  BarChart3, 
  Columns, 
  ClipboardList, 
  Users, 
  Bell, 
  Settings, 
  LogOut, 
  Moon, 
  Sun,
  ChevronDown,
  LayoutDashboard,
  Clock
} from "lucide-react";
import { cn } from "@/lib/utils";

interface SidebarProps {
  currentView: string;
  onViewChange: (view: "dashboard" | "kanban" | "stories" | "active-sprint") => void;
  isOpen: boolean;
  onToggle: () => void;
}

export function Sidebar({ currentView, onViewChange, isOpen, onToggle }: SidebarProps) {
  const { user, logoutMutation } = useAuth();
  const { theme, toggleTheme } = useTheme();

  const getUserInitials = (name: string) => {
    return name
      .split(" ")
      .map(n => n[0])
      .join("")
      .toUpperCase()
      .slice(0, 2);
  };

  const handleLogout = () => {
    logoutMutation.mutate();
  };

  const navigationItems = [
    {
      id: "dashboard",
      label: "Dashboard",
      icon: BarChart3,
      action: () => onViewChange("dashboard"),
    },
    {
      id: "kanban",
      label: "Kanban Board",
      icon: Columns,
      action: () => onViewChange("kanban"),
    },
    {
      id: "active-sprint",
      label: "Active Sprint",
      icon: Clock,
      action: () => onViewChange("active-sprint"),
    },
    {
      id: "stories",
      label: "Stories",
      icon: ClipboardList,
      action: () => onViewChange("stories"),
    },
  ];

  if (!user) return null;

  return (
    <>
      {/* Mobile overlay */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black/50 z-40 md:hidden"
          onClick={onToggle}
        />
      )}

      {/* Sidebar */}
      <div
        className={cn(
          "fixed md:relative z-50 md:z-auto",
          "bg-card border-r border-border w-64 flex-shrink-0 flex flex-col h-full",
          "transition-transform duration-300 ease-in-out",
          isOpen ? "translate-x-0" : "-translate-x-full md:translate-x-0",
          !isOpen && "md:w-0 md:overflow-hidden"
        )}
      >
        {/* Sidebar Header */}
        <div className="p-6 border-b border-border">
          <div className="flex items-center space-x-2">
            <div className="w-8 h-8 bg-primary rounded-lg flex items-center justify-center">
              <LayoutDashboard className="w-4 h-4 text-primary-foreground" />
            </div>
            <div>
              <h1 className="font-semibold text-sm text-foreground">Project Manager</h1>
              <p className="text-xs text-muted-foreground">v1.0.0</p>
            </div>
          </div>
        </div>

        {/* User Info */}
        <div className="p-4 border-b border-border">
          <div className="flex items-center space-x-3">
            <Avatar className="w-8 h-8">
              <AvatarFallback className="bg-accent text-accent-foreground text-sm">
                {getUserInitials(user.name)}
              </AvatarFallback>
            </Avatar>
            <div className="flex-1 min-w-0">
              <p className="text-sm font-medium text-foreground truncate" data-testid="text-username">
                {user.name}
              </p>
              <p className="text-xs text-muted-foreground capitalize" data-testid="text-user-role">
                {user.role}
              </p>
            </div>
            <Button variant="ghost" size="sm" className="w-6 h-6 p-0">
              <ChevronDown className="w-3 h-3 text-muted-foreground" />
            </Button>
          </div>
        </div>

        {/* Navigation Menu */}
        <nav className="flex-1 p-4 space-y-2">
          {navigationItems.map((item) => (
            <Button
              key={item.id}
              variant={currentView === item.id ? "secondary" : "ghost"}
              className="w-full justify-start"
              onClick={item.action}
              data-testid={`button-nav-${item.id}`}
            >
              <item.icon className="w-4 h-4 mr-3" />
              {item.label}
            </Button>
          ))}

          {/* Role-based navigation */}
          {(user.role === "Admin" || user.role === "Team Lead") && (
            <Button
              variant="ghost"
              className="w-full justify-start"
              data-testid="button-nav-team"
            >
              <Users className="w-4 h-4 mr-3" />
              Team Management
            </Button>
          )}

          <Button
            variant="ghost"
            className="w-full justify-start"
            data-testid="button-nav-notifications"
          >
            <Bell className="w-4 h-4 mr-3" />
            Notifications
            <span className="ml-auto bg-destructive text-destructive-foreground text-xs px-2 py-0.5 rounded-full">
              3
            </span>
          </Button>
        </nav>

        {/* Sidebar Footer */}
        <div className="p-4 border-t border-border">
          <div className="flex items-center justify-between">
            <Button
              variant="ghost"
              size="sm"
              onClick={toggleTheme}
              data-testid="button-toggle-theme"
            >
              {theme === "dark" ? (
                <Sun className="w-4 h-4" />
              ) : (
                <Moon className="w-4 h-4" />
              )}
            </Button>
            <Button
              variant="ghost"
              size="sm"
              data-testid="button-settings"
            >
              <Settings className="w-4 h-4" />
            </Button>
            <Button
              variant="ghost"
              size="sm"
              onClick={handleLogout}
              data-testid="button-logout"
            >
              <LogOut className="w-4 h-4" />
            </Button>
          </div>
        </div>
      </div>
    </>
  );
}

client/src/components/story-edit-modal.t.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Separator } from "@/components/ui/separator";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { ScrollArea } from "@/components/ui/scroll-area";
import { 
  Save, 
  X, 
  User, 
  Calendar, 
  Star, 
  Flag, 
  MessageCircle,
  Paperclip,
  Activity,
  Hash,
  CheckSquare,
  Link,
  FileText,
  History,
  ChevronRight,
  ChevronDown,
  Clock
} from "lucide-react";
import { useState, useEffect } from "react";
import { StoryWithAssignee } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/use-auth";
import { cn } from "@/lib/utils";

interface StoryEditModalProps {
  storyId: string;
  open: boolean;
  onClose: () => void;
}

const PRIORITY_OPTIONS = ["Low", "Medium", "High"];
const STATUS_OPTIONS = ["To Do", "In Progress", "Blocked", "Validation", "Completed"];
const STORY_TYPE_OPTIONS = ["Story", "Bug", "Epic"];
const STORY_POINTS = [1, 2, 3, 4, 5];

export function StoryEditModal({ storyId, open, onClose }: StoryEditModalProps) {
  const [formData, setFormData] = useState<Partial<StoryWithAssignee>>({});
  const [showHistory, setShowHistory] = useState(false);
  const [newComment, setNewComment] = useState("");
  const [subtasks, setSubtasks] = useState<Array<{ id: string; text: string; completed: boolean }>>([]);

  const queryClient = useQueryClient();
  const { user } = useAuth();

  const { data: story, isLoading } = useQuery<StoryWithAssignee>({
    queryKey: [`/api/stories/${storyId}`],
    enabled: open && !!storyId,
  });

  const { data: users } = useQuery({
    queryKey: ["/api/users"],
    enabled: open,
  });

  const { data: comments } = useQuery({
    queryKey: [`/api/stories/${storyId}/comments`],
    enabled: open && !!storyId,
  });

  const updateStoryMutation = useMutation({
    mutationFn: async (updates: Partial<StoryWithAssignee>) => {
      const res = await apiRequest("PATCH", `/api/stories/${storyId}`, updates);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/stories"] });
      queryClient.invalidateQueries({ queryKey: [`/api/stories/${storyId}`] });
      onClose();
    },
  });

  const addCommentMutation = useMutation({
    mutationFn: async (comment: string) => {
      const res = await apiRequest("POST", `/api/stories/${storyId}/comments`, {
        commentText: comment,
      });
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/stories/${storyId}/comments`] });
      setNewComment("");
    },
  });

  useEffect(() => {
    if (story) {
      setFormData(story);
      // Initialize subtasks from acceptance criteria if formatted as bullet points
      if (story.acceptanceCriteria) {
        const tasks = story.acceptanceCriteria
          .split('\n')
          .filter(line => line.trim().startsWith('â€¢') || line.trim().startsWith('-'))
          .map((line, index) => ({
            id: `task-${index}`,
            text: line.trim().replace(/^[â€¢-]\s*/, ''),
            completed: false
          }));
        setSubtasks(tasks);
      }
    }
  }, [story]);

  const handleSave = () => {
    updateStoryMutation.mutate(formData);
  };

  const handleAddSubtask = () => {
    setSubtasks([...subtasks, { id: `task-${Date.now()}`, text: '', completed: false }]);
  };

  const updateSubtask = (id: string, updates: Partial<typeof subtasks[0]>) => {
    setSubtasks(subtasks.map(task => task.id === id ? { ...task, ...updates } : task));
  };

  const removeSubtask = (id: string) => {
    setSubtasks(subtasks.filter(task => task.id !== id));
  };

  const getUserInitials = (name: string) => {
    return name?.split(" ").map(n => n[0]).join("").toUpperCase().slice(0, 2) || "U";
  };

  const getStoryNumber = (story: StoryWithAssignee) => {
    return story.storyNumber || `${story.project || "T&D"}-0000`;
  };

  if (!story || isLoading) {
    return null;
  }

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-6xl h-[90vh] p-0">
        <div className="flex h-full">
          {/* Main Content */}
          <div className="flex-1 flex flex-col">
            <DialogHeader className="p-6 border-b border-border">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <Badge variant="outline" className="font-mono">
                    <Hash className="w-3 h-3 mr-1" />
                    {getStoryNumber(story)}
                  </Badge>
                  <DialogTitle className="text-xl">{formData.title}</DialogTitle>
                </div>
                <div className="flex items-center space-x-2">
                  <Button variant="outline" size="sm" onClick={() => setShowHistory(!showHistory)}>
                    <History className="w-4 h-4 mr-2" />
                    History
                  </Button>
                  <Button variant="outline" size="sm" onClick={onClose}>
                    <X className="w-4 h-4" />
                  </Button>
                </div>
              </div>
            </DialogHeader>

            <div className="flex-1 overflow-hidden">
              <div className="grid grid-cols-3 h-full">
                {/* Left Column - Main Details */}
                <div className="col-span-2 p-6 overflow-y-auto">
                  <div className="space-y-6">
                    {/* Title */}
                    <div>
                      <label className="block text-sm font-medium mb-2">Title</label>
                      <Input
                        value={formData.title || ""}
                        onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                        className="text-lg font-medium"
                      />
                    </div>

                    {/* Description with Bullet Points */}
                    <div>
                      <label className="block text-sm font-medium mb-2">Description</label>
                      <Textarea
                        value={formData.description || ""}
                        onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                        placeholder="â€¢ Add bullet points here&#10;â€¢ Use bullet points for better formatting&#10;â€¢ Keep descriptions clear and concise"
                        rows={5}
                        className="font-mono"
                      />
                    </div>

                    {/* Acceptance Criteria - Single Text Box with Bullet Support */}
                    <div>
                      <label className="block text-sm font-medium mb-2">Acceptance Criteria</label>
                      <Textarea
                        value={formData.acceptanceCriteria || ""}
                        onChange={(e) => setFormData({ ...formData, acceptanceCriteria: e.target.value })}
                        placeholder="â€¢ Add acceptance criteria here&#10;â€¢ Use bullet points for better formatting&#10;â€¢ Each line can be a new criteria&#10;â€¢ Press Enter for new lines"
                        rows={8}
                        className="font-mono resize-none"
                      />
                      <p className="text-xs text-muted-foreground mt-1">
                        Use â€¢ or - for bullet points. Press Enter for new lines. No separate "Add Criteria" button needed.
                      </p>
                    </div>

                    {/* Comments Section */}
                    <div>
                      <h3 className="text-sm font-medium mb-3 flex items-center">
                        <MessageCircle className="w-4 h-4 mr-2" />
                        Comments & Activity
                      </h3>
                      <div className="space-y-4">
                        {/* Add Comment */}
                        <div className="flex space-x-3">
                          <Avatar className="w-8 h-8">
                            <AvatarFallback className="text-xs">
                              {getUserInitials(user?.name || "User")}
                            </AvatarFallback>
                          </Avatar>
                          <div className="flex-1 space-y-2">
                            <Textarea
                              value={newComment}
                              onChange={(e) => setNewComment(e.target.value)}
                              placeholder="Add a comment... Use @mention to notify team members"
                              rows={3}
                            />
                            <Button 
                              size="sm" 
                              onClick={() => addCommentMutation.mutate(newComment)}
                              disabled={!newComment.trim()}
                            >
                              Comment
                            </Button>
                          </div>
                        </div>

                        {/* Comments List */}
                        {comments && Array.isArray(comments) && comments.length > 0 ? (
                          comments.map((comment: any) => (
                            <div key={comment.id} className="flex space-x-3 bg-muted/30 rounded-lg p-3">
                              <Avatar className="w-8 h-8">
                                <AvatarFallback className="text-xs">
                                  {getUserInitials(comment.user?.name || "Unknown")}
                                </AvatarFallback>
                              </Avatar>
                              <div className="flex-1">
                                <div className="flex items-center space-x-2 mb-1">
                                  <span className="text-sm font-medium">{comment.user?.name || "Unknown User"}</span>
                                  <span className="text-xs text-muted-foreground">
                                    {comment.createdAt ? new Date(comment.createdAt).toLocaleString("en-US", {
                                      year: 'numeric',
                                      month: 'short',
                                      day: 'numeric',
                                      hour: '2-digit',
                                      minute: '2-digit',
                                      second: '2-digit'
                                    }) : "Just now"}
                                  </span>
                                </div>
                                <p className="text-sm text-foreground whitespace-pre-wrap">{comment.commentText}</p>
                              </div>
                            </div>
                          ))
                        ) : (
                          <div className="text-center text-muted-foreground py-4">
                            <MessageCircle className="w-8 h-8 mx-auto mb-2 opacity-50" />
                            <p className="text-sm">No comments yet</p>
                            <p className="text-xs">Be the first to comment on this story</p>
                          </div>
                        )}
                      </div>
                    </div>

                    {/* Attachments Section */}
                    <div>
                      <h3 className="text-sm font-medium mb-3 flex items-center">
                        <Paperclip className="w-4 h-4 mr-2" />
                        Attachments & Links
                      </h3>
                      <div className="border-2 border-dashed border-border rounded-lg p-6 text-center">
                        <FileText className="w-8 h-8 mx-auto mb-2 text-muted-foreground" />
                        <p className="text-sm text-muted-foreground">
                          Drag files here or click to upload
                        </p>
                        <Button variant="outline" size="sm" className="mt-2">
                          <Link className="w-4 h-4 mr-2" />
                          Add Link
                        </Button>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Right Column - Properties */}
                <div className="border-l border-border p-4 overflow-y-auto bg-muted/30">
                  <div className="space-y-4">
                    {/* Story Type */}
                    <div>
                      <label className="block text-sm font-medium mb-2">Story Type</label>
                      <Select
                        value={formData.storyType || "Story"}
                        onValueChange={(value) => setFormData({ ...formData, storyType: value })}
                      >
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          {STORY_TYPE_OPTIONS.map((type) => (
                            <SelectItem key={type} value={type}>{type}</SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>

                    {/* Priority */}
                    <div>
                      <label className="block text-sm font-medium mb-2">Priority</label>
                      <Select
                        value={formData.priority || "Medium"}
                        onValueChange={(value) => setFormData({ ...formData, priority: value })}
                      >
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          {PRIORITY_OPTIONS.map((priority) => (
                            <SelectItem key={priority} value={priority}>
                              <div className="flex items-center space-x-2">
                                <Flag className="w-4 h-4" />
                                <span>{priority}</span>
                              </div>
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>

                    {/* Story Points */}
                    <div>
                      <label className="block text-sm font-medium mb-2">Story Points</label>
                      <Select
                        value={formData.pointer?.toString() || "1"}
                        onValueChange={(value) => setFormData({ ...formData, pointer: parseInt(value) })}
                      >
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          {STORY_POINTS.map((point) => (
                            <SelectItem key={point} value={point.toString()}>
                              <div className="flex items-center space-x-2">
                                <Star className="w-4 h-4" />
                                <span>{point}</span>
                              </div>
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>

                    {/* Status */}
                    <div>
                      <label className="block text-sm font-medium mb-2">Status</label>
                      <Select
                        value={formData.status || "To Do"}
                        onValueChange={(value) => setFormData({ ...formData, status: value })}
                      >
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          {STATUS_OPTIONS.map((status) => (
                            <SelectItem key={status} value={status}>{status}</SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>

                    {/* Assignee */}
                    <div>
                      <label className="block text-sm font-medium mb-2">Assignee</label>
                      <Select
                        value={formData.assigneeId || "unassigned"}
                        onValueChange={(value) => setFormData({ 
                          ...formData, 
                          assigneeId: value === "unassigned" ? null : value 
                        })}
                      >
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="unassigned">Unassigned</SelectItem>
                          {users && Array.isArray(users) ? users.map((user: any) => (
                            <SelectItem key={user.id} value={user.id}>
                              <div className="flex items-center space-x-2">
                                <User className="w-4 h-4" />
                                <span>{user.name}</span>
                              </div>
                            </SelectItem>
                          )) : null}
                        </SelectContent>
                      </Select>
                    </div>

                    {/* Epic Link */}
                    <div>
                      <label className="block text-sm font-medium mb-2">Epic Link</label>
                      <Input
                        value={formData.epicLink || ""}
                        onChange={(e) => setFormData({ ...formData, epicLink: e.target.value })}
                        placeholder="Link to epic..."
                      />
                    </div>

                    {/* Active Sprint */}
                    <div>
                      <label className="block text-sm font-medium mb-2">Active Sprint</label>
                      <Select
                        value={formData.activeSprint || "sprint-1"}
                        onValueChange={(value) => setFormData({ ...formData, activeSprint: value })}
                      >
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="sprint-1">Sprint 1</SelectItem>
                          <SelectItem value="sprint-2">Sprint 2</SelectItem>
                          <SelectItem value="sprint-3">Sprint 3</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>

                    <Separator />

                    {/* Action Buttons */}
                    <div className="space-y-2">
                      <Button onClick={handleSave} className="w-full">
                        <Save className="w-4 h-4 mr-2" />
                        Save Changes
                      </Button>
                      <Button variant="outline" onClick={onClose} className="w-full">
                        Cancel
                      </Button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Activity History Panel (Collapsible) */}
          {showHistory && (
            <div className="w-80 border-l border-border bg-muted/20">
              <div className="p-4 border-b border-border">
                <div className="flex items-center justify-between">
                  <h3 className="font-medium flex items-center">
                    <Activity className="w-4 h-4 mr-2" />
                    Activity History
                  </h3>
                  <Button 
                    variant="ghost" 
                    size="sm" 
                    onClick={() => setShowHistory(false)}
                  >
                    <X className="w-4 h-4" />
                  </Button>
                </div>
              </div>
              <ScrollArea className="h-[calc(100vh-200px)]">
                <div className="p-4 space-y-4">
                  {/* Sample Activity Items */}
                  <div className="flex space-x-3">
                    <div className="w-2 h-2 bg-blue-500 rounded-full mt-2 flex-shrink-0"></div>
                    <div className="flex-1">
                      <div className="text-sm">
                        <span className="font-medium">Abhishek</span> changed status from 
                        <Badge variant="outline" className="mx-1 text-xs">To Do</Badge> to 
                        <Badge variant="outline" className="mx-1 text-xs">In Progress</Badge>
                      </div>
                      <div className="text-xs text-muted-foreground flex items-center mt-1">
                        <Clock className="w-3 h-3 mr-1" />
                        {new Date(Date.now() - 2 * 60 * 60 * 1000).toLocaleString("en-US", {
                          year: 'numeric',
                          month: 'short',
                          day: 'numeric',
                          hour: '2-digit',
                          minute: '2-digit',
                          second: '2-digit'
                        })}
                      </div>
                    </div>
                  </div>

                  <div className="flex space-x-3">
                    <div className="w-2 h-2 bg-green-500 rounded-full mt-2 flex-shrink-0"></div>
                    <div className="flex-1">
                      <div className="text-sm">
                        <span className="font-medium">Shantnu</span> assigned to 
                        <span className="font-medium mx-1">Tanay</span>
                      </div>
                      <div className="text-xs text-muted-foreground flex items-center mt-1">
                        <Clock className="w-3 h-3 mr-1" />
                        {new Date(Date.now() - 24 * 60 * 60 * 1000).toLocaleString("en-US", {
                          year: 'numeric',
                          month: 'short',
                          day: 'numeric',
                          hour: '2-digit',
                          minute: '2-digit',
                          second: '2-digit'
                        })}
                      </div>
                    </div>
                  </div>

                  <div className="flex space-x-3">
                    <div className="w-2 h-2 bg-purple-500 rounded-full mt-2 flex-shrink-0"></div>
                    <div className="flex-1">
                      <div className="text-sm">
                        <span className="font-medium">Abhishek</span> created this story
                      </div>
                      <div className="text-xs text-muted-foreground flex items-center mt-1">
                        <Clock className="w-3 h-3 mr-1" />
                        {new Date(Date.now() - 3 * 24 * 60 * 60 * 1000).toLocaleString("en-US", {
                          year: 'numeric',
                          month: 'short',
                          day: 'numeric',
                          hour: '2-digit',
                          minute: '2-digit',
                          second: '2-digit'
                        })}
