import { KanbanBoard } from "@/components/kanban-board";
import { ActiveSprint } from "@/components/active-sprint";
import { StoryModal } from "@/components/story-modal";
import { CommentsSidebar } from "@/components/comments-sidebar";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Menu, Search, Plus } from "lucide-react";
import { useAuth } from "@/hooks/use-auth";
import { StoryEditModal } from "@/components/story-edit-modal";

type View = "dashboard" | "kanban" | "stories" | "active-sprint";

export default function HomePage() {
  const [currentView, setCurrentView] = useState<View>("dashboard");
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [showStoryModal, setShowStoryModal] = useState(false);
  const [showComments, setShowComments] = useState(false);
  const [selectedStoryId, setSelectedStoryId] = useState<string | null>(null);
  const [isStoryEditModalOpen, setIsStoryEditModalOpen] = useState(false);
  const [editStoryId, setEditStoryId] = useState<string | null>(null);
  const { user } = useAuth();

  const getViewTitle = () => {
    switch (currentView) {
      case "dashboard":
        return "Dashboard";
      case "kanban":
        return "Board";
      case "stories":
        return "Stories";
      case "active-sprint":
        return "Active Sprint";
      default:
        return "Dashboard";
    }
  };

  const openStoryComments = (storyId: string) => {
    setSelectedStoryId(storyId);
    setShowComments(true);
  };

  const handleStoryEdit = (storyId: string) => {
    setEditStoryId(storyId);
    setIsStoryEditModalOpen(true);
    setSelectedStoryId(null); // Close comments sidebar if open
    setShowComments(false); // Explicitly close comments sidebar
  };

  const handleCloseEdit = () => {
    setIsStoryEditModalOpen(false);
    setEditStoryId(null);
  };

  const handleCloseComments = () => {
    setShowComments(false);
    setSelectedStoryId(null);
  };


  return (
    <div className="flex h-screen overflow-hidden bg-background">
      {/* Sidebar */}
      <Sidebar
        currentView={currentView}
        onViewChange={setCurrentView}
        isOpen={sidebarOpen}
        onToggle={() => setSidebarOpen(!sidebarOpen)}
      />

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Top Bar */}
        <header className="bg-card border-b border-border px-6 py-3 flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Button
              variant="ghost"
              size="sm"
              className="md:hidden"
              onClick={() => setSidebarOpen(!sidebarOpen)}
              data-testid="button-toggle-sidebar"
            >
              <Menu className="w-4 h-4" />
            </Button>
            <h2 className="text-lg font-semibold text-foreground" data-testid="text-page-title">
              {getViewTitle()}
            </h2>
          </div>

          <div className="flex items-center space-x-4">
            {/* Quick Actions */}
            <Button
              onClick={() => setShowStoryModal(true)}
              className="flex items-center space-x-2"
              data-testid="button-new-story"
            >
              <Plus className="w-4 h-4" />
              <span className="hidden sm:inline">New Story</span>
            </Button>
          </div>
        </header>

        {/* Content Area */}
        <main className="flex-1 overflow-hidden">
          {currentView === "dashboard" && <Dashboard />}
          {currentView === "kanban" && (
            <KanbanBoard 
              onStoryClick={handleStoryEdit}
              onCreateStory={() => setShowStoryModal(true)}
            />
          )}
          {currentView === "stories" && <Dashboard />}
          {currentView === "active-sprint" && (
            <ActiveSprint onStoryClick={handleStoryEdit} />
          )}
        </main>
      </div>

      {/* Modals and Sidebars */}
      <StoryModal
        isOpen={showStoryModal}
        onClose={() => setShowStoryModal(false)}
      />

      <CommentsSidebar
        isOpen={showComments}
        onClose={handleCloseComments}
        storyId={selectedStoryId}
        onEditStory={selectedStoryId ? () => handleStoryEdit(selectedStoryId) : undefined}
      />

      {editStoryId && (
        <StoryEditModal
          storyId={editStoryId}
          open={isStoryEditModalOpen}
          onClose={handleCloseEdit}
        />
      )}
    </div>
  );
}

